    private void jButton_CRUD_NVActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_CRUD_NVActionPerformed
        // TODO add your handling code here:

        //NÚT XÓA
        if (jRadioButton_CDXoa.isSelected()){
            String ids=jTextField_MaNV.getText();
            int id=Integer.parseInt(ids.trim());
            if (ControllerAdmin.ktID_NV(id)==0) JOptionPane.showMessageDialog(this, "Nhân Viên không tồn tại, chon 1 o trong DANH SACH de XOA");
            else if (ControllerAdmin.ktIDNV_TK(id)==1 || ControllerAdmin.ktIDNV_DBD(id)==1 || ControllerAdmin.ktIDNV_CTDBD(id)==1){
                JOptionPane.showMessageDialog(this, "Dữ Liệu Nhân Viên đang được sử dụng,KHÔNG ĐƯỢC XÓA");
            }
            else {
                int ret=JOptionPane.showConfirmDialog(this, "Ban co chac chan muon xoa ? ", "Xac Nhan", 0);
                if (ret == JOptionPane.CANCEL_OPTION)
                return;
                else if (ret==JOptionPane.OK_OPTION)
                {
                    ModelAdmin.xoaNV(id);
                    JOptionPane.showMessageDialog(this," Xoa Thanh Cong");
                    this.importDataTKNVPT();
                }
            }
        }
        else {
            String ht=jTextField_TenNV.getText();
            ht=ControllerAdmin.ChuanHoa(ht);
            String cmnd=jTextField_CMNDNV.getText();
            cmnd=cmnd.trim();
            String gt="";
            if (jRadioButton_Nam.isSelected()) gt=jRadioButton_Nam.getText() ; else gt=jRadioButton_Nu.getText();
            String sdt=jTextField_SDTNV.getText();
            sdt=sdt.trim();
            String ns=(String) jComboBox_Ngay.getSelectedItem() +'/'+ (String) jComboBox_Thang.getSelectedItem()+'/' +(String) jComboBox_Nam.getSelectedItem();
            String dn= (String) jComboBox_Ngay.getSelectedItem();
            int d=Integer.parseInt(dn);
            String mn= (String) jComboBox_Thang.getSelectedItem();
            int m=Integer.parseInt(mn);
            String yn= (String) jComboBox_Nam.getSelectedItem();
            int y=Integer.parseInt(yn);

            String td=(String) jComboBox_TrinhDoNV.getSelectedItem();
            boolean kt=true;
            if (ht.equals("") || cmnd.equals("")|| sdt.equals("")){
                kt=false;
                JOptionPane.showMessageDialog(this, "Ho Ten, CMND, SDT Khong Duoc De Trong");
            }
            if (((cmnd.length()!=9 && cmnd.length()!=12) || sdt.length()!=10) && kt){
                kt=false;
                JOptionPane.showMessageDialog(this, "CMND/CCCD có 9/12 chữ số, SDT phải có 10 chữ số");
            }

            if (!ControllerAdmin.Check_Date(d, m, y) && kt){
                kt=false;
                JOptionPane.showMessageDialog(this, "Ngày Tháng Năm Không Hợp Lệ");
            }

            if (kt){
                try {
                    if (jRadioButton_CDSua.isSelected()) {
                        String ids=jTextField_MaNV.getText();
                        int id=Integer.parseInt(ids.trim());
                        int ret=JOptionPane.showConfirmDialog(this, "Ban co chac chan muon Cập Nhật ? ", "Xac Nhan", 0);
                        if (ret == JOptionPane.CANCEL_OPTION)
                        return;
                        else  if (ret==JOptionPane.OK_OPTION) {
                            ModelAdmin.suaNV(id,ht,cmnd,gt,sdt,ns,td);
                            JOptionPane.showMessageDialog(this, "Cập nhật Nhân Viên "+id+" "+ht+" Thành Công");
                        }
                    }
                    else if (jRadioButton_CDThem.isSelected()) {
                         if ((ControllerAdmin.ktCMND_NV(cmnd)==1) && kt){
                            kt=false;
                            JOptionPane.showMessageDialog(this, "CMND Đã Được Thêm, Hãy Thay Đổi");
                        }
                        if ((ControllerAdmin.ktSDT_NV(sdt)==1) && kt){
                            kt=false;
                            JOptionPane.showMessageDialog(this, "SDT Đã Được Đăng Ký, Thay Đổi SDT Khác");
                        }
                        ModelAdmin.themNV(ht,cmnd,gt,sdt,ns,td);
                        JOptionPane.showMessageDialog(this, "Đã Thêm Nhân Viên Thành Công");
                    }
                    this.importDataTKNVPT();
                } catch (ParseException ex) {
                    Logger.getLogger(ViewAdmin.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
    }//GEN-LAST:event_jButton_CRUD_NVActionPerformed

