    private void jTextField_FindID_NVKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField_FindID_NVKeyReleased
        // TODO add your handling code here:
        String s=jTextField_FindID_NV.getText();
        DefaultTableModel dtm= (DefaultTableModel) jTable_DanhSachTKNV.getModel();
        Connection cn=ModelAdmin.connectMSSQL();
        dtm.setNumRows(0);
        String sql="select * from NhanVien order by id";
        String sql1="select NhanVien.id,count(ChiTietDonBaoDuong.idNhanVienPhuTrach) As SoLan\n" + "From NhanVien,ChiTietDonBaoDuong\n" + "where NhanVien.id=ChiTietDonBaoDuong.idNhanVienPhuTrach\n" + "group by NhanVien.id\n" + "order by SoLan Desc";
        Vector vt=null;
        try{
            PreparedStatement ps=cn.prepareStatement(sql);
            ResultSet rs=ps.executeQuery();
            while (rs.next()){
                vt=new Vector();
                String a=rs.getString(1);
                if (a.contains(s)){
                    vt.add(a);
                    vt.add(rs.getString(2));
                    vt.add(rs.getString(3));
                    vt.add(rs.getString(4));
                    vt.add(rs.getString(5));
                    SimpleDateFormat f= new SimpleDateFormat("dd/MM/yyyy");
                    String date=f.format(rs.getDate(6));
                    vt.add(date);
                    vt.add(rs.getString(7));
                    String b="0";
                    PreparedStatement ps1=cn.prepareStatement(sql1);
                    ResultSet rs1=ps1.executeQuery();
                    while (rs1.next()){
                        if (rs1.getString(1).equals(a))
                        {
                            b=rs1.getString(2);
                        }
                    }
                    rs1.close();
                    ps1.close();
                    vt.add(b);
                    dtm.addRow(vt);
                }

            }
            jTable_DanhSachTKNV.setModel(dtm);
            rs.close();
            ps.close();
            cn.close();
        }
        catch (SQLException ex){
            ex.printStackTrace();
        }
    }//GEN-LAST:event_jTextField_FindID_NVKeyReleased

